// Helper function to get cities for a state
export function getCitiesForState(stateId: string): CityData[] {
  return cityData[stateId] || [];
}

// Helper function to get a specific city
export function getCity(cityId: string): CityData | undefined {
  for (const cities of Object.values(cityData)) {
    const city = cities.find(c => c.id === cityId);
    if (city) return city;
  }
  return undefined;
}
