export interface CityData {
  id: string;
  name: string;
  county: string;
  type: 'city' | 'county';
  population: number;
  rpr: number; // Revenue-to-Price Ratio (annual gross revenue / purchase price) - top-level for easy access
  dsi: boolean; // Debt Survivability Index - can deal survive 10-15% down at current rates?
  marketScore: {
    overall: number;
    demand: number;
    affordability: number;
    regulation: number;
    seasonality: number;
    saturation: number; // 0-100 score (100 = low saturation/good, 0 = high saturation/bad)
    rpr: number; // Revenue-to-Price Ratio score (0-100, heaviest weighted)
    verdict: 'strong-buy' | 'buy' | 'hold' | 'caution' | 'avoid';
  };
  // Investment Viability Metrics
  investmentMetrics: {
    rpr: number; // Revenue-to-Price Ratio (annual gross revenue / purchase price)
    rprRating: 'elite' | 'good' | 'marginal' | 'poor'; // elite > 0.18, good > 0.15, marginal > 0.12, poor < 0.12
    dsi: boolean; // Debt Survivability Index - can deal survive 10-15% down at current rates?
    dsiDetails: {
      monthlyMortgage: number; // Estimated monthly mortgage payment
      monthlyExpenses: number; // Operating expenses (cleaning, utilities, PM, insurance, taxes)
      netMonthlyIncome: number; // Revenue - mortgage - expenses
      survives: boolean; // Net income > 0
    };
  };
  saturationRisk: {
    strToHousingRatio: number; // Percentage of housing units that are STRs
    listingsPerThousand: number; // Active STR listings per 1,000 residents
    yoySupplyGrowth: number; // Year-over-year growth in STR supply (%)
    riskLevel: 'low' | 'moderate' | 'high' | 'very-high';
  };
  rental: {
    avgADR: number;
    occupancyRate: number;
    monthlyRevenue: number;
    medianHomePrice: number;
    // Percentile income data
    revenue75thPercentile: number; // Top 25% performers
    revenue90thPercentile: number; // Top 10% performers
    // Mid-term rental (MTR) data - 30+ day furnished rentals
    mtrMonthlyIncome: number; // Average MTR monthly income
  };
  // Income by Property Size - monthly revenue estimates
  incomeBySize: {
    oneBR: number;   // 1 bedroom
    twoBR: number;   // 2 bedroom
    threeBR: number; // 3 bedroom
    fourBR: number;  // 4 bedroom
    fiveBR: number;  // 5 bedroom
    sixPlusBR: number; // 6+ bedroom
    bestPerformer: '1BR' | '2BR' | '3BR' | '4BR' | '5BR' | '6BR+'; // Which size performs best in this market
  };
  // Amenity Delta - which amenities boost revenue most in this market
  amenityDelta: {
    topAmenities: Array<{
      name: string; // e.g., "Hot Tub", "Pool", "Game Room"
      revenueBoost: number; // Percentage boost (e.g., 15 means +15%)
      priority: 'must-have' | 'high-impact' | 'nice-to-have';
    }>;
    marketType: 'mountain' | 'beach' | 'urban' | 'lake' | 'desert' | 'rural' | 'suburban' | 'waterfront' | 'tropical'; // Helps determine amenity recommendations
  };
  strStatus: 'legal' | 'restricted' | 'varies' | 'banned';
  permitRequired: boolean;
  highlights: string[];
}

